import { useState } from "react"
import axios from "axios"

export default function WeatherAPI(){
    var [state,setState]=useState()
    var [loc,setLoc]=useState("")
    function demo(e){
        setLoc(e.target.value)
    }
    function submitHandler(e){
        e.preventDefault()
        axios.get(location)
            .then((res)=>{setState(Math.round(res.data.main.temp-273.15))})
    }
    var location="https://api.openweathermap.org/data/2.5/weather?q="+loc+"&appid=2ddeea50df238d55467a5990c451cc76"
    return(
        <>
        <input type="text" onChange={demo}/>
        <h3>Location: {loc}</h3>
        <h2>Temperature: {state}<sup>o</sup>C</h2>
        <form onSubmit={submitHandler}>
        <input type="submit"/>
        </form>
        
        </>
    );
}
