import { useState } from 'react';
import Data from '../data/loginCredentials.json'
import pic from '../images/logoLoginPage.jpg'

export default function LoginPage(){
    var [emailId,setEmailId]=useState("")
    var [password,setPassword]=useState("")
    var [data,setData]=useState(Data)
    
    var emailFunction=(e)=>{
        setEmailId(e.target.value)
    }
    var passwordFunction=(e)=>{
        setPassword(e.target.value)
    }
    var loginStatus
    function check()
    {
        {data.map((x)=>{
            if((emailId===x.email)&&(password===x.password))
                {
                    loginStatus="Login Successful"   
                }
                else{
                    loginStatus="Unauthorized Access"
                    setTimeout(()=>alert("Access Denied"),1000)
                }
        }
    )}
        
    }
    
    return(
        <>
        <form onSubmit={check}>
            <div className='row'>
                <div className='col-lg-12'>
                    <img src={pic} className='img-fluid d-block mx-auto'/>
                </div>         
            </div>
            <div className='row'>
                <div className='col-lg-12'>
                    <div class="mb-3">
                        <label for="email" class="form-label">Email</label>
                        <input type="email" class="form-control" id="username" placeholder="Enter email id" onChange={emailFunction}/>
                    </div>
                </div>
            </div>
            <div className='row'>
                <div className='col-lg-12'>
                    <div class="mb-3">
                        <label for="password" class="form-label">Password</label>
                        <input type="password" class="form-control" id="password" placeholder="Enter password" onChange={passwordFunction}/>
                    </div>
                </div>
            </div>
            <div className='row'>
                <div className='col-lg-12'>
                    <div class="mb-3">
                        <input type="submit" class="btn btn-outline-danger"/>
                    </div>
                </div>
            </div>
            <h1>{loginStatus}</h1>
        </form>
        </>
    );
}
