import { useEffect, useState } from "react";
import '../../node_modules/bootstrap/dist/css/bootstrap.css'
import axios from "axios";
import '../styles/food.css' 

export default function Food(){
    var [categories,setCategories]=useState([])
    var [selectedCategory,setSelectedCategory]=useState("")
    var [meals,setMeals]=useState([])
    useEffect(()=>{
        axios.get("https://www.themealdb.com/api/json/v1/1/categories.php")
        .then((res)=>{
            setCategories(res.data.categories)
        })
    })
    function getMeals(){
            axios.get(`https://www.themealdb.com/api/json/v1/1/filter.php?c=${selectedCategory}`)
            .then((res)=>{
                setMeals(res.data.meals)
        })
    }
    return(
        <>
        <div className="container-fluid">
            <div className="row">
                {categories.map((category)=>
                    <div className="col-md-4 col-lg-3 col-sm-6">
                        <div class="card category-card">
                            <img src={category.strCategoryThumb} class="card-img-top category-image d-block mx-auto" alt="category image"/>
                            <div class="card-body">
                                <h5 class="card-title">{category.strCategory}</h5>
                                <button value={category.strCategory.toLowerCase()} class="btn btn-danger"><a href="#" class="btn btn-danger" onClick={getMeals}>Get Meals</a></button>
                            </div>
                        </div>
                    </div>
                )}
            </div>
        </div>
        </>
    );
}
