import pic from '../images/logoSignUpPage.jpg'
import { useState,useEffect } from 'react'
import signUpCredentialsList from '../data/signUpCredentials'

export default function SignUpPage(){
    var [emailId,setEmailId]=useState("")
    var [password,setPassword]=useState("")
    
    var emailFunction=(e)=>{
        setEmailId(e.target.value)
    }
    var passwordFunction=(e)=>{
        setPassword(e.target.value)
    }
    function updateData()
    {
        useEffect(()=>{
        if(emailId!==""&&password!==""){
            signUpCredentialsList.pop()
        alert("Sign Up Successful")
        }
        else
        alert("Invalid Credentials")
    }
)
}
    return(
        <>
        <form onSubmit={updateData}>
            <div className='row'>
                <div className='col-lg-12'>
                    <img src={pic} className='img-fluid d-block mx-auto'/>
                </div>         
            </div>
            <div className='row'>
                <div className='col-lg-12'>
                    <div class="mb-3">
                        <label for="email" class="form-label">Email</label>
                        <input type="email" class="form-control" id="username" placeholder="Enter email id" onChange={emailFunction}/>
                    </div>
                </div>
            </div>
            <div className='row'>
                <div className='col-lg-12'>
                    <div class="mb-3">
                        <label for="password" class="form-label">Password</label>
                        <input type="password" class="form-control" id="password" placeholder="Enter password" onChange={passwordFunction}/>
                    </div>
                </div>
            </div>
            <div className='row'>
                <div className='col-lg-12'>
                    <div class="mb-3">
                        <input type="submit" class="btn btn-outline-danger"/>
                    </div>
                </div>
            </div>
        </form>
        </>
    );
}
