import axios from "axios";
import { useState } from "react";

export default function News(){
    var [news,setNews]=useState([])
    var [sources,setSources]=useState([])
        function getNewsByCategory(e){
            axios.get(`https://saurav.tech/NewsAPI/top-headlines/category/${e.target.value}/in.json`)
            .then((res)=>{
                setNews(res.data.articles)
            })
}
        axios.get(`https://saurav.tech/NewsAPI/sources.json`)
        .then((res)=>{
            setSources(res.data.sources)
        })
    return(
        <>
        <div className="container-fluid">
            <div className="row">
                <div className="col-lg-12 my-2"><input type="text" className="form-control" placeholder="Search news"/></div>
            </div>
        </div>
        <div className="container-fluid">
            <div className="row">
                <div className="col-lg-2 my-2">
                    <select onChange={getNewsByCategory} className="form-select">
                        <option selected>Choose a category</option>
                        <option value="business">Business</option>
                        <option value="entertainment">Entertainment</option>
                        <option value="general">General</option>
                        <option value="health">Health</option>
                        <option value="science">Science</option>
                        <option value="sports">Sports</option>
                        <option value="technology">Technology</option>
                    </select>
            </div>
            <div className="col-lg-2 my-2">
                <select className="form-select" onChange={getNewsBySource}>
                {sources.map((x)=>
                <>
                <option selected>Our Sources</option>
                <option value={x.id}>{x.name}</option>
                </>   
                )}
                </select>
            </div>
            </div>
        </div>
        <div className="container-fluid">
            <div className="row">
                <div className="col-lg-12">
                    {news.map((x)=><h6>{x.title}</h6>)}
                </div>
            </div>
        </div>
        </>
    );
}
