import { useState } from "react"
import axios from "axios"

export default function WeatherAPI(){
    var [state,setState]=useState()
    var [loc,setLoc]=useState("")
    var[coordinates,setCoordinates]=useState({})
    var[weather,setWeather]=useState([])
    function demo(e){
        setLoc(e.target.value)
    }
    function submitHandler(e){
        e.preventDefault()
        axios.get(location)
            .then((res)=>{setState(Math.round(res.data.main.temp-273.15))
                setCoordinates(res.data.coord)
                setWeather(res.data.weather)
            })

            
    }
    var location="https://api.openweathermap.org/data/2.5/weather?q="+loc+"&appid=2ddeea50df238d55467a5990c451cc76"
   
    return(
        <>
        <div className="row">
            <div className="col-lg-12">
            <input type="text" className="form-control m-3" placeholder="Enter city name" onChange={demo}/>
            </div>
        </div>
        <div className="row">
            <div className="col-lg-6">
                <h2>Location: {loc}</h2>
            </div>
            <div className="col-lg-6">
                <h2>Temperature: {state}<sup>o</sup>C</h2>
            </div>
        </div>
        <div className="row">
            <div className="col-lg-6">
                <h2>Longitude: {coordinates.lon}</h2>
            </div>
            <div className="col-lg-6">
                <h2>Latitude: {coordinates.lat}</h2>
            </div>
        </div>
        <div className="row">
            <div className="col-lg-3">
                {weather.map((x)=><h2>Weather: {x.main}</h2>)}
            </div>
            <div className="col-lg-3">
                {weather.map((x)=><h2>Weather Description: {x.description}</h2>)}
            </div>
            <div className="col-lg-3">
                {weather.map((x)=>{
                    var iconID=x.icon
                    var iconURL="http://openweathermap.org/img/w/"+iconID+".png";
                    return( 
                        <img src={iconURL}/>
                        )
                        }
                    )
                }
            </div>
        </div>
        
        
        
        
        
        
        
        <form onSubmit={submitHandler}>
        <input type="submit" className="btn btn-outline-danger ms-10px"/>
        </form>
        
        </>
    );
}
