import axios from "axios";
import { useState } from "react";

export default function News(){
    var [news,setNews]=useState([])
    var [search,setSearch]=useState("")
    function search(e){
        setSearch(e.target.value)
    }
    axios.get(`https://newsdata.io/api/1/latest?apikey=pub_48205f4fe927256206346a5098a9186e4b689&q=${search}&language=en`)
    .then((res)=>{
        setNews(res.data.results)
    })
    return(
    <>
    <input placeholder="Search News" className="form-control"/>
    <button onClick={search}>Get</button>
    <h3>News Component</h3>
    {news.map((x)=><h4>{news.language}</h4>)}
    </>);
}
