import axios from "axios";
import { useState } from "react";
import '../styles/news.css'

export default function News(){
    var [news,setNews]=useState([])
    var [search,setSearch]=useState("")
    var [news2,setNews2]=useState([])
    var [source,setSource]=useState([])
    var [category,setCategory]=setCategory("")
    function searchData(e){
        setSearch(e.target.value)
        axios.get(`https://saurav.tech/NewsAPI/top-headlines/category/${category}/in.json`)
        .then((res2)=>{
            setNews2(res2.data.articles)
        })
    }
    function selectFunction(e){
        setCategory(e.target.value)
    }
    axios.get(`https://saurav.tech/NewsAPI/top-headlines/category/business/in.json`)
    .then((res)=>{
        setNews(res.data.articles)
    })
    axios.get("https://saurav.tech/NewsAPI/sources.json")
    .then((res3)=>{
        setSource(res3.data.sources)
    })
    return(
    <>
    <div className="container-fluid bg-success-subtle">
        <div className="row">
            <div className="col-lg-12">
                <input placeholder="Search News" className="form-control my-3" onChange={searchData}/>
            </div>
        </div>
        <div className="row">
            <div className="col-lg-6">
                <select class="form-select form-select-lg mb-3" onSelect={selectFunction}>
                    <option selected>Select category</option>
                    <option value="business">Business</option>
                    <option value="entertainment">Entertainment</option>
                    <option value="health">Health</option>
                    <option value="science">Science</option>
                    <option value="sports">Sports</option>
                    <option value="technology">Technology</option>
                </select>
            </div>
        </div>
        <div className="row">
            <div className="col-lg-12">
                <h6>Our Sources include:</h6>
                {source.map((x)=>{
                    if(x.name.length<10){
                        return(x.name+", ")
                    }
            })}
            etc.
            </div>
        </div>
        <div className="row">
            {
            news2.filter((item)=>item.title.includes(search)).map((x)=>
                        <div className="col-lg-4">
                            <div class="card">
                                <img class="card-img-top" src={x.urlToImage} alt="image"/>
                                <div class="card-body">
                                    <h5 class="card-title">{x.title}</h5>
                                    <p class="card-text">{x.description}</p>
                                    <a href={x.url} class="btn btn-primary">Get News</a>
                                </div>
                            </div>
                        </div>
            )}
        </div>
        <div>
            <hr/>
        </div>
        <div className="row">
            {
            news.map((x)=>
                        <div className="col-lg-4">
                            <div class="card">
                                <img class="card-img-top" src={x.urlToImage} alt="image"/>
                                <div class="card-body">
                                    <h5 class="card-title">{x.title}</h5>
                                    <p class="card-text">{x.description}</p>
                                    <a href={x.url} class="btn btn-primary">Get News</a>
                                </div>
                            </div>
                        </div>
            )}
        </div>
    </div>
    </>);
}
