import axios from "axios";
import { useState } from "react";
import '../styles/news.css'

export default function News(){
    var [news,setNews]=useState([])
    var [search,setSearch]=useState("")
    var [news2,setNews2]=useState([])
    var [source,setSource]=useState([])
    function searchData(e){
        setSearch(e.target.value)
        axios.get(`https://newsapi.org/v2/everything?q=latest&apiKey=6f17844250a9424aa7bd567639ecbd17&language=en`)
        .then((res2)=>{
            setNews2(res2.data.articles)
        })
    }
    axios.get(`https://saurav.tech/NewsAPI/top-headlines/category/business/in.json`)
    .then((res)=>{
        setNews(res.data.articles)
    })
    axios.get("https://saurav.tech/NewsAPI/sources.json")
    .then((res3)=>{
        setSource(res3.data.sources)
    })
    return(
    <>
    <div className="container-fluid bg-success-subtle">
        <div className="row">
            <div className="col-lg-12">
                <input placeholder="Search News" className="form-control my-3" onChange={searchData}/>
            </div>
        </div>
        <div className="row">
            <div className="col-lg-12">
                <h6>Some of our sources :</h6>
                {source.map((x)=>{
                    if(x.name.length<10){
                        return(x.name)
                    }
            })}
            </div>
        </div>
        <div className="row">
            {
            news2.filter((item)=>item.title.includes(search)).map((x)=>
                        <div className="col-lg-4">
                            <div class="card">
                                <img class="card-img-top" src={x.urlToImage} alt="image"/>
                                <div class="card-body">
                                    <h5 class="card-title">{x.title}</h5>
                                    <p class="card-text">{x.description}</p>
                                    <a href={x.url} class="btn btn-primary">Get News</a>
                                </div>
                            </div>
                        </div>
            )}
        </div>
        <div>
            <hr/>
        </div>
        <div className="row">
            {
            news.map((x)=>
                        <div className="col-lg-4">
                            <div class="card">
                                <img class="card-img-top" src={x.urlToImage} alt="image"/>
                                <div class="card-body">
                                    <h5 class="card-title">{x.title}</h5>
                                    <p class="card-text">{x.description}</p>
                                    <a href={x.url} class="btn btn-primary">Get News</a>
                                </div>
                            </div>
                        </div>
            )}
        </div>
    </div>
    </>);
}
