import axios from "axios";
import { useState } from "react";

export default function News(){
    var [news,setNews]=useState([])
    var [search,setSearch]=useState("")
    function searchFunction(e){
        setSearch(e.target.value)
    }
    axios.get(`https://newsapi.org/v2/everything?q=latest&apiKey=6f17844250a9424aa7bd567639ecbd17`)
    .then((res)=>{
        setNews(res.data.articles)
    })
    return(
    <>
    <input placeholder="Search News" className="form-control"/>
    <button onClick={searchFunction} className="btn btn-outline-danger">Get</button>
    <h3>News Component</h3>
    {news.map((x)=><h4>{x.source.id}</h4>)}
    </>);
}
