import axios from "axios";
import { useState } from "react";

export default function News(){
    var [news,setNews]=useState([])
        function getNewsByCategory(e){
        axios.get(`https://saurav.tech/NewsAPI/top-headlines/category${e.target.value}/in.json`)
        .then((res)=>{
            setNews(res.data.articles)
        })
    }
    return(
        <>
        <div>
            <input type="text" className="form-control" placeholder="Search news"/>
        </div>
        <div>
            <select onChange={getNewsByCategory}>
                <option>business</option>
                <option>entertainment</option>
                <option>general</option>
                <option>health</option>
                <option>science</option>
                <option>sports</option>
                <option>technology</option>
            </select>
        </div>
        <div className="container-fluid">
            <div className="row">
                <div className="col-lg-12">
                    {news.map((x)=><h6>{x.title}</h6>)}
                </div>
            </div>
        </div>
        </>
    );
}
