import axios from "axios";
import { useState } from "react";
import '../styles/weather.css'

export default function Weather(){
    var [coordinates,setCoordinates]=useState({})
    var [weather,setWeather]=useState([])
    var [main,setMain]=useState({})
    var [wind,setWind]=useState({})
    var [clouds,setClouds]=useState({})
    axios.get("https://api.openweathermap.org/data/2.5/weather?q=Kadapa&appid=55a0ebcb240d607f077934dec3f7822a")
    .then((res)=>{
        setCoordinates(res.data.coord)
        setWeather(res.data.weather)
        setMain(res.data.main)
        setWind(res.data.wind)
        setClouds(res.data.clouds)
    })
    var iconCode=weather.map((x)=>x.icon)
    var iconLink="http://openweathermap.org/img/w/" + iconCode + ".png"
    return(
        <>
        <div className="container-fluid">
            <div className="row">
                <div className="col-lg-12 first-row">
                    <div className="flex-items">
                        <h3>Latitude: {coordinates.lat}</h3>
                        <h3>Longitude: {coordinates.lon}</h3>
                    </div>
                    <div className="flex-items">
                        {weather.map((x)=>
                        <>
                    <h3>Weather: {x.main}</h3>
                    <h3>Description: {x.description}</h3>
                    <img src={iconLink}/>    
                    </>
                    )
                        }
                    </div>
                </div>
            </div>
        </div>
        </>
    );
}
