import { useState } from "react"
import axios from "axios"
import '../styles/weatherAPI.css'

export default function WeatherAPI(){
    var [state,setState]=useState()
    var [loc,setLoc]=useState("")
    var[coordinates,setCoordinates]=useState({})
    var[weather,setWeather]=useState([])
    function demo(e){
        setLoc(e.target.value)
    }
    function submitHandler(e){
        e.preventDefault()
        axios.get(location)
            .then((res)=>{setState(Math.round(res.data.main.temp-273.15))
                setCoordinates(res.data.coord)
                setWeather(res.data.weather)
            })

            
    }
    var location="https://api.openweathermap.org/data/2.5/weather?q="+loc+"&appid=2ddeea50df238d55467a5990c451cc76"
   
    return(
        <>
        <div className="row bg-primary-subtle">
            <div className="col-lg-12">
            <input type="text" className="form-control mt-3" placeholder="Enter city name" onChange={demo}/>
            </div>
        </div>
        <div className="row mt-2">
            <div className="col-lg-6">
                <h2 className="weather">Location: {loc}</h2>
            </div>
            <div className="col-lg-6">
                <h2 className="weather">Temperature: {state}<sup>o</sup>C</h2>
            </div>
        </div>
        <div className="row mt-2">
            <div className="col-lg-6">
                <h2 className="weather">Longitude: {coordinates.lon}</h2>
            </div>
            <div className="col-lg-6">
                <h2 className="weather">Latitude: {coordinates.lat}</h2>
            </div>
        </div>
        <div className="row mt-2">
            <div className="col-lg-3">
                {weather.map((x)=><h2 className="weather">Weather: {x.main}</h2>)}
            </div>
            <div className="col-lg-6">
                {weather.map((x)=><h2 className="weather">Weather Description: {x.description}</h2>)}
            </div>
            <div className="col-lg-3">
                {weather.map((x)=>{
                    var iconID=x.icon
                    var iconURL="http://openweathermap.org/img/w/"+iconID+".png";
                    return( 
                        <img src={iconURL}/>
                        )
                        }
                    )
                }
            </div>
        </div>
        
        <form onSubmit={submitHandler}>
        <input type="submit" className="btn btn-outline-danger m-3"/>
        </form>
        
        </>
    );
}
